class Main():
  count = 0
  def __init__(self, gold):
    self.gold = gold
    self.number = Main.count
    Main.count += 1
  def __str__(self):
    return "Main(" + str(self.number) + "): " + str(self.gold)
  def add(self, diff_gold):
    self.gold += diff_gold
  def is_negative(self):
    return self.gold < 0
  def counterValue():
    return Main.count

